mapper.select:
		SELECT count(*) 
  		FROM MEMBER
		WHERE NICKNAME = #{nickname}

		SELECT count(*) 
  		FROM MEMBER
		WHERE EMAIL = #{email}

mapper.update:
		UPDATE MEMBER
   		   SET M_PASS = #{m_pass},
	      	   NICKNAME = #{nickname},
	           PHONE = #{phone},
	           BIRTH = #{birth}
	     WHERE EMAIL = #{email}

UPDATE MEMBER
   SET M_USEABLE = '불가'
 WHERE EMAIL = #{email}

UPDATE MEMBER
   SET M_PASS = #{m_pass}
 WHERE EMAIL = #{email}

mapper.select:
SELECT COUNT(*) FROM MEMBER
 WHERE EMAIL = #{email}
   AND M_PASS = #{m_pass}
